import React, { useEffect, useState } from 'react';
import { Link, useLocation } from 'react-router-dom'; // or remove/use <a> if not using React Router

const navLinks = [
  { name: 'Home', path: '/' },
  { name: 'About Us', path: '/about' },
  { name: 'Services', path: '/services' },
  { name: 'Portfolio', path: '/portfolio' },
  { name: 'Blog', path: '/blog' },
  { name: 'Contact', path: '/contact' },
];

export default function Navbar() {
  const [scrolled, setScrolled] = useState(false);
  const [open, setOpen] = useState(false);
  const location = useLocation(); // if using React Router

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 40) setScrolled(true);
      else setScrolled(false);
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  // close mobile menu on route change
  useEffect(() => {
    setOpen(false);
  }, [location.pathname]);

  return (
    <header
      className={`fixed top-0 left-0 w-full z-40 transition-all duration-300 ${
        scrolled ? 'bg-white/90 shadow-md backdrop-blur-sm' : 'bg-white'
      }`}
    >
      <nav className="max-w-6xl mx-auto flex items-center justify-between px-4 py-3">
        {/* Logo / Brand */}
        <div className="flex items-center gap-2">
          <div className="w-9 h-9 rounded-full bg-black flex items-center justify-center text-white font-bold">
            SK
          </div>
          <span className="text-xl font-semibold text-pink-700 tracking-wide">
            Elite Events
          </span>
        </div>

        {/* Desktop Links */}
        <ul className="hidden md:flex items-center gap-8 text-base">
          {navLinks.map((link) => {
            const isActive = location.pathname === link.path;
            return (
              <li key={link.name} className="relative group">
                <Link
                  to={link.path}
                  className={`transition-colors duration-200 ${
                    isActive ? 'text-pink-700 font-semibold' : 'text-gray-800'
                  }`}
                >
                  {link.name}
                </Link>
                {/* underline animation */}
                <span
                  className={`absolute left-0 -bottom-1 h-0.5 bg-pink-600 origin-left transform transition-transform duration-300 ${
                    isActive ? 'scale-x-100' : 'scale-x-0 group-hover:scale-x-100'
                  }`}
                  style={{ width: '100%' }}
                />
              </li>
            );
          })}
          {/* CTA button */}
          <li>
            <Link
              to="/contact"
              className="ml-4 px-4 py-2 rounded-full bg-pink-700 text-white text-sm font-semibold shadow hover:bg-pink-800 transition"
            >
              Get a Quote
            </Link>
          </li>
        </ul>

        {/* Mobile Hamburger */}
        <button
          className="md:hidden flex flex-col gap-1.5 focus:outline-none"
          onClick={() => setOpen((prev) => !prev)}
          aria-label="Toggle navigation"
        >
          <span
            className={`h-0.5 w-6 bg-gray-800 transition-transform duration-300 ${
              open ? 'translate-y-1.5 rotate-45' : ''
            }`}
          />
          <span
            className={`h-0.5 w-6 bg-gray-800 transition-opacity duration-300 ${
              open ? 'opacity-0' : 'opacity-100'
            }`}
          />
          <span
            className={`h-0.5 w-6 bg-gray-800 transition-transform duration-300 ${
              open ? '-translate-y-1.5 -rotate-45' : ''
            }`}
          />
        </button>
      </nav>

      {/* Mobile Menu */}
      <div
        className={`md:hidden overflow-hidden transition-all duration-300 ${
          open ? 'max-h-96' : 'max-h-0'
        }`}
      >
        <ul className="flex flex-col bg-white/95 backdrop-blur-sm border-t border-gray-100 px-4 pb-4">
          {navLinks.map((link) => {
            const isActive = location.pathname === link.path;
            return (
              <li key={link.name} className="border-b border-gray-100 last:border-none">
                <Link
                  to={link.path}
                  className={`block py-3 text-sm ${
                    isActive ? 'text-pink-700 font-semibold' : 'text-gray-800'
                  }`}
                >
                  {link.name}
                </Link>
              </li>
            );
          })}
          <li className="pt-2">
            <Link
              to="/contact"
              className="block text-center w-full px-4 py-2 rounded-full bg-pink-700 text-white text-sm font-semibold shadow hover:bg-pink-800 transition"
            >
              Get a Quote
            </Link>
          </li>
        </ul>
      </div>
    </header>
  );
}
